interface.definition:
a!localVariables(
  {
    #"SYSTEM_SYSRULES_richTextDisplayField"(
      value: {
        #"SYSTEM_SYSRULES_richTextIcon"(
          icon: "times",
          link: #"SYSTEM_SYSRULES_dynamicLink"(saveInto: { a!save(ri!showCard, false()) }),
          linkStyle: "STANDALONE",
          color: if(ri!isSuccess, "", "NEGATIVE"),
          size: "SMALL"
        )
      },
      showWhen: ri!showCard,
      align: "RIGHT",
      marginBelow: "NONE"
    ),
    #"SYSTEM_SYSRULES_cardLayout"(
      contents: {
        #"SYSTEM_SYSRULES_richTextDisplayField"(
          value: {
            #"SYSTEM_SYSRULES_richTextItem_v1"(
              text: if(
                ri!isSuccess,
                {
                  #"SYSTEM_SYSRULES_richTextHeader"(text: "Chat Completion"),
                  #"SYSTEM_SYSRULES_richTextBulletedList"(
                    items: ri!completions
                  )
                },
                {

                  #"SYSTEM_SYSRULES_richTextIcon"(icon: "times", size: "MEDIUM"),
                  #"SYSTEM_SYSRULES_richTextItem_v1"(
                    text: {
                      "  Error occured: ",
                      index(
                        ri!error,
                        "title",
                        "Something went wrong"
                      )
                    },
                    style: "STRONG"
                  ),
                  #"SYSTEM_SYSRULES_richTextItem_v1"(
                    text: { char(10), index(ri!error, "message", "") }
                  ),
                  #"SYSTEM_SYSRULES_richTextItem_v1"(
                    text: { char(10), index(ri!error, "detail", "") }
                  )
                }
              ),
              color: if(ri!isSuccess, "STANDARD", "NEGATIVE"),
              size: ""

            )
          },
          marginBelow: "EVEN_LESS"
        )
      },
      showWhen: ri!showCard,
      style: if(ri!isSuccess, "#C0C0C0", "ERROR")
    )
  }
)
